xvfb-run --server-args="-screen 0 1024x768x24" mvn clean install test -DsuiteXmlFile=SUITE_NAME > logs/automation.log || (cd target/ ; mv surefire-reports/html BITBUCKCET_BUILD_NUMBER ; scp -o "StrictHostKeyChecking=no" -r BITBUCKCET_BUILD_NUMBER root@taf-reports.cltp.com:/var/www/html/platform/ENV/APP_NAME ; sed -i '13i<div id="meta"> Hi Team,  <br /> Please find the automation report status. <br /> Test  result report : <a href="http://taf-reports.cltp.com/platform/ENV/APP_NAME/BITBUCKCET_BUILD_NUMBER" target="_top">http://taf-reports.cltp.com/platform/ENV/APP_NAME/BITBUCKCET_BUILD_NUMBER</a> <br /><br /> </div>' BITBUCKCET_BUILD_NUMBER/overview.html ; sed -i 's/<\/body>/<div id="meta"><br \/><br \/> Thanks,  <br \/> Automation Team <\/body>/g' BITBUCKCET_BUILD_NUMBER/overview.html ; mail -s "$(echo -e "Automation Report - SUBJECT-ENV\nContent-Type: text/html")" MAIL_ID < BITBUCKCET_BUILD_NUMBER/overview.html && exit 1)
